static VOID ct_im_r2y_start_b2r( UCHAR pipe_no )
{
	gct_im_r2y_b2r_end_flg = FALSE;
	Im_B2R_Start( pipe_no, D_IM_B2R_START_EXTERNAL_IF_ONLY );
}

static VOID ct_im_r2y_wait_b2r( UCHAR pipe_no )
{
	UINT32 loopcnt;

	for( loopcnt = 0; gct_im_r2y_b2r_end_flg == FALSE; loopcnt++ ) {
		if( loopcnt >= 10 ) {
			Ddim_Print(( "B2R wait end timeout.\n" ));
			break;
		}

		DDIM_User_Dly_Tsk( 1 );
#ifdef CO_DEBUG_ON_PC
		ct_im_r2y_b2r_int_handler( 0, 0 );
#endif
	}

	gct_im_r2y_b2r_end_flg = FALSE;
}

static VOID ct_im_r2y_waitend_stop_b2r( UCHAR pipe_no )
{
	ct_im_r2y_wait_b2r( pipe_no );

	Im_B2R_Stop( pipe_no );
}


