static VOID ct_im_r2y_start_ltm( UCHAR pipe_no )
{
	gct_im_r2y_ltm_end_flg = FALSE;
	Im_LTM_LTM_Start( pipe_no );
}

static VOID ct_im_r2y_wait_ltm( UCHAR pipe_no )
{
	UINT32 loopcnt;
#ifdef CO_DEBUG_ON_PC
	UINT32 result = 1;
#endif

	for( loopcnt = 0; gct_im_r2y_ltm_end_flg == FALSE; loopcnt++ ) {
		if( loopcnt >= 10 ) {
			Ddim_Print(( "B2R wait end timeout.\n" ));
			break;
		}

		DDIM_User_Dly_Tsk( 1 );
#ifdef CO_DEBUG_ON_PC
		ct_im_r2y_ltm_int_handler( &result, pipe_no );
#endif
	}

	gct_im_r2y_ltm_end_flg = FALSE;
}

static VOID ct_im_r2y_waitend_stop_ltm( UCHAR pipe_no )
{
	ct_im_r2y_wait_ltm( pipe_no );

	Im_LTM_LTM_Stop( pipe_no );
}


static VOID ct_im_r2y_set_wb_gain_rgb( UCHAR pipe_no )
{
	static T_IM_R2Y_RGB_COLOR im_r2y_wb_gain = {
		(USHORT)(1.0F * 0x100),	// R
		(USHORT)(1.0F * 0x100),	// G
		(USHORT)(1.0F * 0x100),	// B
	};
	INT32	im_ercd;
	im_ercd = Im_R2Y_Set_WB_Gain( pipe_no, &im_r2y_wb_gain );
	if( im_ercd != D_DDIM_OK ) {
		Ddim_Print(( "Im_R2Y_Set_WB_Gain() error=%d\n", im_ercd ));
	}
}

