			Ddim_Print(( D_IM_R2Y_FUNC_NAME "%d bank_idx=%u\n", ercd, bank_index ));

			ercd = Im_R2Y_Get_OutBankIndex( 1, D_IM_R2Y_YYW_CH_2, &bank_index );
